<!button class="btn btn-primary" data-enable-captcha="true" data-trigger="5a999cc45e5cac33663efaa1" onclick="submitOrCaptcha(event, this)"!><!/button!><script type="text/javascript">var enableCaptcha = true

// Options for the popup that opens for the captcha (600 by 400, centered on the screen)
var popupOptions = function(width, height) {
  var obj = {
    width: width,
    height: height,
    left: (screen.width / 2) - (width / 2),
    top: (screen.height / 2) - (height / 2)
  }
  return Object.keys(obj).reduce(function(memo, key) {
    memo.push(key + '=' + obj[key])
    return memo
  }, []).join(', ')
}

// The URL the popup window will point to
var popupUrl = "https://editor.orson.io/admin/97522b42/forms/captcha"
var popup;

var submitOrCaptcha = function(event, element) {
  if (element.dataset.trigger) {
    var widgetId = element.dataset.trigger
    // Dataset seems to only store strings
    if (element.dataset.enableCaptcha !== 'true') {
      document.querySelector('form[data-widget-id="' + widgetId + '"]').submit()
      return true;
    } else {
      event.preventDefault()
      popup = window.open(popupUrl + '?origin=' + window.location.origin + '&widget_id=' + widgetId, 'captcha', popupOptions(400,600))
    }
  }
}

// Event handler for when we receive information from the popup
// Get the token from the CAPTCHA and submit the form with the value
window.addEventListener('message', function(event) {
  try {
    var data = JSON.parse(event.data)
    if (data.hasOwnProperty('token') && data.hasOwnProperty('captchaPopup') && data.captchaPopup) {
      var token = data.token
      popup.close()
      var input = document.createElement('input')
      input.type = "hidden"
      input.name = 'g-recaptcha-response'
      input.value = token
      var form = document.querySelector('form[data-widget-id="' + data.widgetId + '"]')
      form.appendChild(input)
      form.submit()
    }
  } catch(e) {

  }
}, false)

var getSearchParams = function(search) {
  var search = search.slice(1).split('&').map(function(segment) {
    var parts = segment.split('=')
    return [parts[0], parts[1]]
  })
  return search.reduce(function(memo, element) {
    memo[element[0]] = element[1]
    return memo
  }, {})
}

var filterSearch = function(search) {
  return Object.keys(search)
  .filter(function(key) {
    return key !== 'notice'
  })
  .reduce(function(memo, key) {
    memo.push(key + '=' + search[key])
    return memo
  }, [])
  .join('&')
}

var getField = function(name, widgetId) {
  var inputs = 'form[data-widget-id="' + widgetId + '"] input[data-field-for="' + name + '"]'
  var textareas = 'form[data-widget-id="' + widgetId + '"] textarea[data-field-for="' + name + '"]'
  var inputField = document.querySelector([inputs, textareas].join(', '))
  return inputField
}
window.addEventListener('load', function(event) {
  if(!window.handledNotices) {
    window.handledNotices = true
    var searchParams = getSearchParams(window.location.search)
    if (searchParams.hasOwnProperty('notice')) {
      var notice = JSON.parse(decodeURIComponent(searchParams.notice))
      var widgetIdentifier = notice.widget_id
      // SUCCESS
      if (notice.success) {
        var notificationArea = document.querySelector('p.alert.alert-success[data-for-widget="' + widgetIdentifier + '"]')
        notificationArea.style.display = 'block'
        notificationArea.innerText = notice.message
      } else {
        // ERROR
        var values = notice.values
        Object.keys(values).forEach(function(key) {
          var inputField = getField(key, widgetIdentifier)
          if (inputField) {
            inputField.value = values[key]
            if (notice.errors.indexOf(key) !== -1) {
              inputField.classList.add('validation-error')
            }
          }
          return true
        })
      }
      var filteredSearch = filterSearch(searchParams)
      if (filteredSearch.length > 0) {
        window.history.pushState({notice: notice}, "", location.pathname + '?' + filteredSearch)
      } else {
        window.history.pushState({notice: notice}, "", location.pathname)
      }  
    } 
  }
})</script></div></div><div class="widgetContainer"><div class="textContainer"><h4><strong>Already registered?</strong><a href="/2/pricing"><strong> </strong></a><a href="/2/pricing" style="color: rgb(13, 8, 173);"><strong>Log In</strong></a><strong> here</strong></h4></div></div></div></div></div></section></div><footer><section class="bgSection pi-custom-color pi-text-light spacing-top-1  spacing-bottom-1" style=""><div class="bgSectionBackgroundColor" style="background-color: #000000; opacity: 1"></div><div class="container"><div class="row-fluid"><div class="span12"><div class="widgetContainer"><div class="sharelinkContainer text-center"><a class="sharelink sharelink-facebook sharelink-color-white sharelink-style-circle sharelink-size" href="https://www.facebook.com/kwallet" target="_blank" title="Facebook"><i aria-hidden="true" class="fa fa-facebook"></i></a><a class="sharelink sharelink-twitter sharelink-color-white sharelink-style-circle sharelink-size" href="https://twitter.com/OrsonFrench" target="_blank" title="Twitter"><i aria-hidden="true" class="fa fa-twitter"></i></a><a class="sharelink sharelink-google sharelink-color-white sharelink-style-circle sharelink-size" href="https://plus.google.com/+Pikock" target="_blank" title="Google"><i aria-hidden="true" class="fa fa-google-plus"></i></a><a class="sharelink sharelink-mail sharelink-color-white sharelink-style-circle sharelink-size" href="mailto:kanigupta99@gmail.com" target="_blank" title="Mail"><i aria-hidden="true" class="fa fa-envelope"></i></a></div></div><div class="widgetContainer"><div class="textContainer"><h4 class="ql-align-center">Copyright © K-Wallet 2018</h4><p class="ql-align-center">With&nbsp;<strong>♥ From K-Wallet</strong>&nbsp;<br></p></div></div><div class="widgetContainer"><div class="textContainer"><p class="ql-align-center"><a href="http://support@kwallet.com" target="_blank">support@kwallet.com</a></p><p class="ql-align-center">+918178218180</p></div></div></div></div></div></sect